// HomePage.jsx
import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import SearchBar from "../SearchBar/SearchBar";
import Cards from "../Cards/Cards";
import { buscarConductor } from '../actions';

const HomePage = ({ conductores, buscarConductor }) => {
  useEffect(() => {
    // Realizar una búsqueda inicial de conductores al montar el componente
    buscarConductor('');
  }, [buscarConductor]);

  return (
    <div>
      <h1>Home Page</h1>
      <h2>La mejor página de la historia la encontrarás en Home Page</h2>
      <SearchBar onSearch={buscarConductor} />
      <Cards drivers={conductores} />
    </div>
  );
};

// Función para mapear el estado de Redux a props
const mapStateToProps = (state) => {
  return {
    conductores: state.conductores // Suponiendo que tu estado de Redux tiene una propiedad llamada conductores
  };
};

// Función para mapear las acciones de Redux a props
const mapDispatchToProps = (dispatch) => {
  return {
    buscarConductor: (nombre) => dispatch(buscarConductor(nombre))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(HomePage);
