import React, { useState } from "react";
import SearchBar from "../SearchBar/SearchBar";
import Cards from "../Cards/Cards";

const HomePage = ({ onSearch, drivers }) => {
  const conductores = drivers.length > 0 ? drivers[0].conductores : [];
  const [conductoresFiltrados, setConductoresFiltrados] = useState(conductores);
  const [equipoSeleccionado, setEquipoSeleccionado] = useState('');

  // Función para manejar la búsqueda por nombre
  const handleSearch = (conductoresPorNombre) => {
    setConductoresFiltrados(conductoresPorNombre);
  };

  // Función para manejar el cambio en la selección del equipo
  const handleEquipoChange = (event) => {
    const equipoSeleccionado = event.target.value;
    setEquipoSeleccionado(equipoSeleccionado);
  };

  // Función para manejar el clic en el nombre del equipo
  const handleEquipoClick = (equipoNombre) => {
    const conductoresFiltrados = conductores.filter(conductor =>
      conductor.equipos.some(equipo => equipo.Nombre === equipoNombre)
    );
    setEquipoSeleccionado(equipoNombre);
    setConductoresFiltrados(conductoresFiltrados);
  };

  // Obtener todos los equipos disponibles de los conductores
  const equiposDisponibles = [...new Set(conductores.flatMap(conductor => conductor.equipos.map(equipo => equipo.Nombre)))];

  return (
    <div>
      <h1>Home Page</h1>
      <h2>La mejor pagina de la historia la encontraras en Home Page</h2>
      {/* Componente SearchBar con función de búsqueda y resultados */}
      <SearchBar onSearch={handleSearch} />

      {/* Select para seleccionar el equipo */}
      <select value={equipoSeleccionado} onChange={handleEquipoChange}>
        <option value="">Selecciona un equipo</option>
        {/* Opciones de equipo disponibles */}
        {equiposDisponibles.map((equipo, index) => (
          <option key={index} value={equipo}>{equipo}</option>
        ))}
      </select>

      {/* Botones para filtrar por equipo */}
      <div>
        {equiposDisponibles.map((equipo, index) => (
          <button key={index} onClick={() => handleEquipoClick(equipo)}>{equipo}</button>
        ))}
      </div>

      {/* Componente Cards con el array de conductores filtrados */}
      <Cards drivers={conductoresFiltrados || []} />
    </div>
  );
}

export default HomePage;
