import SearchBar from "../SearchBar/SearchBar";
import Cards from "../Cards/Cards";
import { useState } from "react";

const HomePage = ({ onSearch, drivers }) => {
    
    const conductores = drivers.length > 0 ? drivers[0].conductores : [];
    const [conductoresFiltrados, setConductoresFiltrados] = useState(conductores);
    const [equipoSeleccionado, setEquipoSeleccionado] = useState('');
    const [conductoresBuscados, setConductoresBuscados] = useState([]);

    const filtrarPorEquipo = (event) => {
        const equipoSeleccionado = event.target.value;
        setEquipoSeleccionado(equipoSeleccionado);

        // Filtrar conductores por equipo seleccionado
        const conductoresFiltradosPorEquipo = conductores.filter(conductor => {
            return conductor.equipos.some(equipo => equipo.Nombre === equipoSeleccionado);
        });

        setConductoresFiltrados(conductoresFiltradosPorEquipo);
    };

    const handleSearch = (searchTerm) => {
        // Actualizar el estado de conductores buscados
        setConductoresBuscados(searchTerm);

        // Ejecutar la búsqueda de conductores por nombre utilizando onSearch
        onSearch(searchTerm).then(conductoresFiltradosPorNombre => {
            setConductoresFiltrados(conductoresFiltradosPorNombre);
        });
    };

    const equiposDisponibles = [...new Set(conductores.flatMap(conductor => conductor.equipos.map(equipo => equipo.Nombre)))];

    return (
        <div>
            <h1>Home Page</h1>
            <h2>La mejor pagina de la historia la encontraras en Home Page</h2>       
            <SearchBar onSearch={handleSearch} />
            <select value={equipoSeleccionado} onChange={filtrarPorEquipo}>
                <option value="">Selecciona un equipo</option>
                {equiposDisponibles.map((equipo, index) => (
                    <option key={index} value={equipo}>{equipo}</option>
                ))}
            </select>
            <Cards drivers={conductoresFiltrados} />
        </div>
    );
}

export default HomePage;
