import SearchBar from "../SearchBar/SearchBar";
import Cards from "../Cards/Cards";
import { useState } from "react";

const HomePage = ({ onSearch, drivers }) => {
    console.log("HomePage:", drivers);
    const [filtrarEquipos, setFiltrarEquipos] = useState('');

    const handleFiltrar = (equipo) => {
        setFiltrarEquipos(equipo);
    }

    // Obtener la lista de equipos disponibles
    const equipos = drivers.map(driver => driver.conductores)
        .flat()
        .reduce((acc, conductor) => {
            conductor.equipos.forEach(equipo => {
                if (!acc.includes(equipo.Nombre)) {
                    acc.push(equipo.Nombre);
                }
            });
            return acc;
        }, []);

    // Filtrar conductores según el equipo seleccionado
    const conductoresFiltrados = drivers.map(driver => driver.conductores)
        .flat()
        .filter(conductor => {
            return (!filtrarEquipos || conductor.equipos.some(equipo => equipo.Nombre === filtrarEquipos));
        });

    return (
        <div>
            <h1>Home Page</h1>
            <h2>La mejor página de la historia la encontrarás en Home Page</h2>
            <SearchBar onSearch={onSearch} />
            <select value={filtrarEquipos} onChange={(e) => handleFiltrar(e.target.value)}>
                <option value="">Seleccionar equipo</option>
                {equipos.map((equipo, index) => (
                    <option key={index} value={equipo}>{equipo}</option>
                ))}
            </select>
            <button>Ordenar</button>
            <Cards drivers={drivers} />
        </div>
    );
}

export default HomePage;
