import { OBTENER_DETALLES_CONDUCTOR } from "./action-types.js"
import axios from "axios"


  
  export const obtenerDetallesConductor = (idDriver) => {
    return async (dispatch) => {
      try {
        // Realizar la solicitud HTTP al backend para obtener detalles del conductor
        const response = await axios.get(`http://localhost:3001/drivers/${idDriver}`);
        
        // Una vez que se reciben los datos, dispatchar la acción con los detalles del conductor como payload
        dispatch({
          type: OBTENER_DETALLES_CONDUCTOR,
          payload: response.data // Suponiendo que el backend responde con los detalles del conductor
        });
      } catch (error) {
        console.error("Error al obtener los detalles del conductor:", error);
        // Podrías dispatchar una acción adicional para manejar el error si es necesario
      }
    };
  };
