import { BUSCAR_CONDUCTOR, OBTENER_DETALLES_CONDUCTOR } from "./action-types.js";

const initialState = {
  conductores: [],
  detalleConductor: null
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case BUSCAR_CONDUCTOR:
      return {
        ...state,
        conductores: action.payload 
      };
    case OBTENER_DETALLES_CONDUCTOR:

      return {
        ...state,
        detalleConductor: action.payload 
      };
    default:
      return state;
  }
};

export default rootReducer;
