import SearchBar from "../SearchBar/SearchBar";
import Cards from "../Cards/Cards";
import { useState } from "react";

const HomePage = ({ onSearch, drivers }) => {
  const [filtrarEquipos, setFiltrarEquipos] = useState(""); // Estado para almacenar el equipo seleccionado

  // Función para manejar el cambio en la selección del equipo
  const handleFiltrar = (event) => {
    setFiltrarEquipos(event.target.value); // Actualizar el estado con el equipo seleccionado
  };

  // Obtener una lista de equipos únicos
  const equipos = drivers.map(driver => driver.conductores).flat().reduce((acc, conductor) => {
    conductor.equipos.forEach(equipo => {
      if (!acc.includes(equipo.Nombre)) {
        acc.push(equipo.Nombre);
      }
    });
    return acc;
  }, []);

  // Filtrar los conductores basados en el equipo seleccionado
  const conductoresFiltrados = drivers.map(driver => driver.conductores).flat().filter(conductor => {
    return (!filtrarEquipos || conductor.equipos.some(equipo => equipo.Nombre === filtrarEquipos));
  });

  return (
    <div>
      <h1>Home Page</h1>
      <h2>La mejor pagina de la historia la encontraras en Home Page</h2>       
      <SearchBar onSearch={onSearch} />

      {/* Menú desplegable para seleccionar equipos */}
      <select value={filtrarEquipos} onChange={handleFiltrar}>
        <option value="">Seleccionar equipo</option>
        {/* Mapear cada equipo único */}
        {equipos.map((equipo, index) => (
          <option key={index} value={equipo}>{equipo}</option>
        ))}
      </select>

      <button>Ordenar</button>

      {/* Renderizar los conductores filtrados */}
      <Cards drivers={conductoresFiltrados} />
    </div>
  );
}

export default HomePage;
